"use strict";

const {
  slash
} = require('./utils');

const getEdgioConfigFilePath = require('./getEdgioConfigFilePath');

module.exports = function getEdgioConfig(filePath) {
  const file = filePath || getEdgioConfigFilePath();

  if (file) {
    return require(slash(file));
  } else {
    return null;
  }
};